function shareTimeline(service) {
  dipity.analytics.trackEvent('share_timeline', service, 'A Timeline of Contexts and Events for Early American Literature');
  if (service == "twitter")      { window.open("http://twitter.com/share?url=http%3A%2F%2Fwww.dipity.com%2Febb8%2FEarly-American-Literature%2F%3Fs=t&text=Check+out+%22A+Timeline+of+Contexts+and+Events+for+Early+American+Literature%22+on+%40dipity."); }
  else if (service == "fb")      { window.open("http://www.facebook.com/sharer.php?u=http%3A%2F%2Fwww.dipity.com%2Febb8%2FEarly-American-Literature%2F&title=A+Timeline+of+Contexts+and+Events+for+Early+American+Literature%3Fs=fb"); }
  else if (service == "digg")    { window.open("http://digg.com/submit?phase=2&url=http%3A%2F%2Fwww.dipity.com%2Febb8%2FEarly-American-Literature%2F&title=A+Timeline+of+Contexts+and+Events+for+Early+American+Literature%3Fs=d"); }
  else if (service == "myspace") { window.open("http://www.myspace.com/Modules/PostTo/Pages/?t=A+Timeline+of+Contexts+and+Events+for+Early+American+Literature&u=http%3A%2F%2Fwww.dipity.com%2Febb8%2FEarly-American-Literature%2F%3Fs=m"); }
  else if (service == "su")      { window.open("http://www.stumbleupon.com/submit?url=http%3A%2F%2Fwww.dipity.com%2Febb8%2FEarly-American-Literature%2F&title=A+Timeline+of+Contexts+and+Events+for+Early+American+Literature%3Fs=su"); }

  return false;
}

